document.addEventListener("mousemove",parallax);const parallaxThings=document.querySelectorAll(".leaderboard__parallax");function parallax(e){parallaxThings.forEach((t=>{const a=t.getAttribute("data-position"),o=(window.innerWidth-e.pageX*a)/100,n=(window.innerHeight-e.pageY*a)/100;t.style.transform=`translateX(${o}px) translateY(${n}px)`}))}function t(e=storageMode){return JSON.parse(localStorage.getItem(`${e}-leaderboard`))}const chooseMode=document.getElementById("gameMode"),storageMode=localStorage.getItem("mode"),list=document.getElementById("leaderList");chooseMode.addEventListener("change",(()=>{list.innerHTML="",setScores(t(chooseMode.value))}));const setScores=e=>{for(let t in e){const a=document.createElement("li");a.classList.add("leaderboard__list--item");const o=e[t].highScore;a.innerHTML=`\n    <span id="leaderBoardName">${t}:&nbsp;</span>\n    <span id="leaderBoardScore">${o}</span>\n  `,list.append(a)}};chooseMode.value=storageMode??"attack",setScores(t());